import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html'
})
export class LoginComponent {
  user!: IUser;
  users: IUser[] = [];


  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<IUser[]>('https://localhost:7158/user').subscribe(result => {
      this.users = result;
    }, error => console.error(error));
  }

  public login() {
    console.log('working...', this.users);

  }

}

interface IUser {
  username: string;
  password: number;
}
<div class="example-card">
   <header>
     <title>Login</title>
   </header>
   <div>
     <form class="example-form">
       <table class="example-full-width" cellspacing="0">
         <tr>
           <td>
             <div class="example-full-width">
             <input placeholder="Username" [(ngModel)]="user.username" name="username" required>
             </div>
           </td>
         </tr>
         <tr>
         <td><div class="example-full-width">
           <input placeholder="Password" [(ngModel)]="user.password"type="password" name="password" required>
         </div></td>
       </tr></table>
     </form>
     <!--<div [style.display]="user ? 'block' : 'none'"></div>-->
   </div>
   <div>
     <button (click)="login()" color="primary">Login</button>
   </div>
 </div>
